      // Convert ethers signer to Universal Signer
      const universalSigner = await PushChain.utils.signer.toUniversal(signer);
      console.log('ðŸ”‘ Got universal signer - Ethers');
      console.log(JSON.stringify(universalSigner));
    }

    await main().catch(console.error);

`}

</NodeJSVirtualIDE>

  </TabItem>

  <TabItem value='viem' label='Viem'>

<NodeJSVirtualIDE repo={{title: "Open in Github", url: "https://github.com/pushchain/push-chain-examples/tree/main/core-sdk-functions/create-universal-signer"}}>

