// customPropHighlightRegexStart=<PushUniversalWalletProvider\s
// customPropHighlightRegexEnd=</PushUniversalWalletProvider>
// customPropMinimized='false'
// Import necessary components from @pushchain/ui-kit
import { PushChain } from '@pushchain/core';
import {
  PushUniversalWalletProvider,
  PushUniversalAccountButton,
  usePushWalletContext,
  usePushChainClient,
  PushUI,
} from '@pushchain/ui-kit';
import { useState } from 'react';

function App() {
  // Define Wallet Config
  const walletConfig = {
    network: PushUI.CONSTANTS.PUSH_NETWORK.TESTNET,
    login: {
      email: true,
      google: true,
      wallet: {
        enabled: true,
      },
      appPreview: true,
    },
    modal: {
      loginLayout: PushUI.CONSTANTS.LOGIN.LAYOUT.SPLIT,
      connectedLayout: PushUI.CONSTANTS.CONNECTED.LAYOUT.HOVER,
      appPreview: true,
    },
  };

  // Define Your App Preview
  const appMetadata = {
    logoUrl:
      'https://plus.unsplash.com/premium_photo-1746731481770-08b2f71661d0?q=80&w=2671&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D',
    title: 'Test App Title',
    description: 'Test App Description',
  };

  function WalletUI() {
    const [txnHash, setTxnHash] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    const { connectionStatus } = usePushWalletContext();
    const { pushChainClient } = usePushChainClient();

    const handleSendTransaction = async () => {
      if (pushChainClient) {
        setIsLoading(true);
        try {
          const res = await pushChainClient.universal.sendTransaction({
            to: '0xFaE3594C68EDFc2A61b7527164BDAe80bC302108',
            value: PushChain.utils.helpers.parseUnits('0.01', 18), // 0.01 PC
          });
          setTxnHash(res.hash);
        } catch (err) {
          console.log(err);
        } finally {
          setIsLoading(false);
        }
      }
    };

    return (
      <div>
        <PushUniversalAccountButton />
        {connectionStatus == PushUI.CONSTANTS.CONNECTION.STATUS.CONNECTED && (
          <button
            disabled={isLoading}
            style={{
              background: 'transparent',
              border: '1px solid',
              borderRadius: '10px',
              padding: '12px 18px',
              cursor: 'pointer',
              marginTop: '8px',
            }}
            onClick={handleSendTransaction}
          >
            Send Transaction
          </button>
        )}
        {txnHash && (
          <>
            <p>Txn Hash: {txnHash}</p>
            <a
              href={pushChainClient?.explorer.getTransactionUrl(txnHash)}
              target='_blank'
              rel='noopener noreferrer'
            >
              View in Explorer
            </a>
          </>
        )}
      </div>
    );
  }

  return (
    <PushUniversalWalletProvider config={walletConfig} app={appMetadata}>
      <WalletUI />
    </PushUniversalWalletProvider>
  );
}
```

